import React, { Fragment } from 'react'
import "./projetUn.css"
import jscss from'../../../../../image/js.jpg'
import { useInView } from 'react-intersection-observer';
import { useEffect } from 'react';
import { useRef } from 'react';


export default function ProjetUn() {

  const { ref:refImgContainer1,inView:visible1 } = useInView({threshold:1,triggerOnce:true});
  const refImgFlecheGauche1 = useRef();
  const refApparitionBoutton1=useRef();
  const refContainerTexteProjet1=useRef();
  const refContainerImg1=useRef();
  const refTitreProjet1 =useRef();

  
  useEffect(() => {
  if(visible1){
  //  refTitreProjet1.current.classList.add('apparitionTitreScroll');
  //  refContainerTexteProjet1.current.classList.add('apparitionFlecheScroll');
  //  refContainerImg1.current.classList.add('apparitionContainerImg');
  }
  }, [visible1])
  

  const apparaitionBouttonDisparitionImg1 =()=>{
    refImgFlecheGauche1.current.classList.remove('visibleBoutton');
    refApparitionBoutton1.current.classList.remove('invisibleBoutton');
  }

  const disparitionBoutton1 =()=>{
    refApparitionBoutton1.current.classList.remove('visibleBoutton');
    refApparitionBoutton1.current.classList.add('invisibleBoutton');
  }


  return (
    
    <div id='projetUn'>
      <p id='titreProjet1' ref={refTitreProjet1}> Projet css-javascript</p>
      <div className='containerProjet1'>
        <div  id="containerTexteProjet1" className='containerTexteProjet1'>
            <p id='texteProjet1'>
               Passionné par le web design et des animations c'est tout naturellement que j'ai fais mes premiers pas dans le développement en css et javascript.C'est donc logiquement que je vous propose différents projets sur des animations de bouttons, de containers, d'images et une animation du système solaire.
            </p>
        </div>
        <div  ref={refContainerImg1} className="containerInvisible"  id='ContainerImageProjet1' onMouseLeave={disparitionBoutton1} onMouseEnter={apparaitionBouttonDisparitionImg1} >
          <img  src={jscss}></img>
          <button ref={refApparitionBoutton1} className='invisibleDiv'>Détails</button>
        </div>
      </div>
      <div ref={refImgContainer1} className='inferieur1'></div>
    </div>
    
    
  )
}
