            <p></p>
            <p></p>
            <p></p>
       </div>
       <div id='divBouttonPassion'>
          <button ref={refBoutonGauche} onClick={mouvementGauche}>gauche</button>
          <button>droite</button>
       </div>
    </section>
  )
}
