import React from 'react'
import { Fragment } from 'react'
import '../projet/projet.css';
import jscss from'../../../../image/js.jpg';
import { useRef } from 'react';
import java from'../../../../image/java.jpg';
import reacts from'../../../../image/react.jpg';
import { useInView } from 'react-intersection-observer';
import { useEffect } from 'react';
import { useState } from 'react';


export default function Projet() {

const { ref:refDivInvisible,inView:divIsVisible } = useInView({threshold:1,triggerOnce:true});
 
  const [texteVisible, settexteVisible] = useState(false);

 const refContainerImageProjet1=useRef();
 const refTexteProjet1 =useRef();
 const refBouttonContainerProjet =useRef();
  useEffect(() => {
    if(divIsVisible){
        refContainerImageProjet1.current.classList.add('affichageContainerProjet');
        setTimeout(() => {
          settexteVisible(true);
        }, 1000);
    }
    
  }, [divIsVisible])
  
  const affichageTexte =()=>{
    if(texteVisible){
      refBouttonContainerProjet.current.classList.remove('invisible');
      refBouttonContainerProjet.current.classList.remove('cacherBouttonContainerProjet');
      refBouttonContainerProjet.current.classList.add('afficheBouttonContainerProjet');
      refTexteProjet1.current.classList.add('decalageGaucheTexteProjet');
      refTexteProjet1.current.classList.remove('decalageDroiteTexteProjet');
      refTexteProjet1.current.classList.add('visible');
      refTexteProjet1.current.classList.remove('invisible');
      refContainerImageProjet1.current.classList.add('mouvementContainerProjet');
    }
   
  }

  const cacherTexte =()=>{
    refBouttonContainerProjet.current.classList.add('cacherBouttonContainerProjet');
    refBouttonContainerProjet.current.classList.remove('afficheBouttonContainerProjet');
    refTexteProjet1.current.classList.remove('decalageGaucheTexteProjet');
    refTexteProjet1.current.classList.add('decalageDroiteTexteProjet');
    refTexteProjet1.current.classList.remove('visible');
    refTexteProjet1.current.classList.add('invisible');
  }

  return (
    <Fragment>
        <div className='premierProjet'>
            <p  className='titreProjet'> Projet css-javascript</p>
            <div className='containerProjet '>
                <div  className='containerTexteProjet1'>
                    <p ref={refTexteProjet1} className='texteProjet1 invisible'>
                      Passionné du web design et des animations c'est tout naturellement que j'ai fais mes premiers pas dans le développement en css et javascript.C'est donc logiquement que je vous propose différents projets sur des animations de bouttons, de containers, d'images et une animation du système solaire.
                    </p>
                </div>
                <div ref={refContainerImageProjet1} onMouseEnter={affichageTexte} onMouseLeave={cacherTexte} className=' ContainerImageProjet1 invisible'>
                    <img   src={jscss}></img>
                    <button ref={refBouttonContainerProjet}  className="invisible">Détails</button>
                </div>
            </div>
            <div ref={refDivInvisible} className='divInvisible'></div>  
        </div> 

    </Fragment>
  )
}
