import React from 'react'
import './containerPageProjetCssJs.css'
import { Fragment } from 'react'
import ReactPlayer from 'react-player'
import video from '../../../../video/test.mp4'
import { useState } from 'react'
import { useRef } from 'react'


export default function ContainerPageProjetCssJs() {

const refTitreParaPageProjet= useRef();
const [titreProjet, settitreProjet] = useState("projet 1")
const apparitionDetails= ()=>{
  refTitreParaPageProjet.current.classList.add("animAccrocheContainerProjetInvisible");
  setTimeout(() => {
    settitreProjet("cliquez pour plus de détails.");
  }, 2200);
  setTimeout(() => {
    refTitreParaPageProjet.current.classList.add("animAccrocheContainerProjetVisible");
  }, 2500);
 }
  return (
    <Fragment>
      <div  className='titreParaPageProjet'>
        <span>Mes projets css-javascript</span>
     </div>
     <div  onMouseEnter={apparitionDetails} className='contientProjetJs'>
        <span ref={refTitreParaPageProjet}>{titreProjet}</span>
        <div className='descriptionProjetsJs'>

        </div>
     </div>

    </Fragment>
)
}
