import React from 'react'
import "../element/element.css"
import { Fragment } from 'react';
import { useRef } from 'react';
import { useInView } from 'react-intersection-observer';
import { useEffect } from 'react';


export default function Element() {
  const { ref:myref,inView:visible } = useInView({threshold:0.5});
  const reference=useRef();
  useEffect(()=>{
    reference.current.classList.add("start2");
  },[])

  return (
    <Fragment>
          <div ref={reference} className='start1' >
             
          </div> 
          <div ref={myref} className={visible==false? 'boby':'bonjour'} >
             
          </div>  
    </Fragment>
  )
}
