import React from 'react'
import { Fragment } from 'react'
import '../projetDeux/projetDeux.css';
import java from'../../../../../image/java.jpg';
import { useRef } from 'react';
import { useInView } from 'react-intersection-observer';
import { useEffect } from 'react';
import { useState } from 'react';


export default function Projet() {

const { ref:refDivInvisible,inView:divIsVisible } = useInView({threshold:1,triggerOnce:true});
 
  const [texteVisible, settexteVisible] = useState(false);

 const refContainerImageProjet2=useRef();
 const refTexteProjet2 =useRef();
 const refBouttonContainerProjet2 =useRef();
 const TitreContainerProjet2 =useRef();

  useEffect(() => {
    if(divIsVisible){
        refContainerImageProjet2.current.classList.add('affichageContainerProjet2');
        TitreContainerProjet2.current.classList.add('affichageTitreProjet');
        setTimeout(() => {
          settexteVisible(true);
        }, 1000);
    }
    
  }, [divIsVisible])
  
  const affichageTexte =()=>{
    if(texteVisible){
      refBouttonContainerProjet2.current.classList.remove('invisible');
      refBouttonContainerProjet2.current.classList.remove('cacherBouttonContainerProjet');
      refBouttonContainerProjet2.current.classList.add('afficheBouttonContainerProjet');
      refTexteProjet2.current.classList.add('decalageDroiteTexteProjet2');
      refTexteProjet2.current.classList.remove('decalageGaucheTexteProjet2');
      refTexteProjet2.current.classList.add('visible');
      refTexteProjet2.current.classList.remove('invisible');
      refContainerImageProjet2.current.classList.add('mouvementContainerProjet');
    }
   
  }

  const cacherTexte =()=>{
    refBouttonContainerProjet2.current.classList.add('cacherBouttonContainerProjet');
    refBouttonContainerProjet2.current.classList.remove('afficheBouttonContainerProjet');
    refTexteProjet2.current.classList.remove('decalageDroiteTexteProjet2');
    refTexteProjet2.current.classList.add('decalageGaucheTexteProjet2');
    refTexteProjet2.current.classList.remove('visible');
    refTexteProjet2.current.classList.add('invisible');
  }

  return (
    <Fragment>
        <div className='deuxiemeProjet'>
            <p ref={TitreContainerProjet2}  className='titreProjet2 invisible'> Projet java</p>
            <div className='containerProjet2 '>
                <div ref={refContainerImageProjet2} onMouseEnter={affichageTexte} onMouseLeave={cacherTexte} className=' ContainerImageProjet2 invisible'>
                    <img   src={java}></img>
                    <button ref={refBouttonContainerProjet2}  className="invisible">Détails</button>
                </div>
                <div  className='containerTexteProjet2'>
                    <p ref={refTexteProjet2} className='texteProjet2 invisible'>
                      Passionné du web design et des animations c'est tout naturellement que j'ai fais mes premiers pas dans le développement en css et javascript.C'est donc logiquement que je vous propose différents projets sur des animations de bouttons, de containers, d'images et une animation du système solaire.
                    </p>
                </div>
            </div>
            <div ref={refDivInvisible} className='divInvisible'></div>  
        </div> 

    </Fragment>
  )
}
