import React from 'react'
import "./competences.css"
import { useEffect } from 'react'
import { useRef } from 'react'
import { useInView } from 'react-intersection-observer'

export default function Competences() {

    const { ref:refDivInvisible15,inView:visible15 } = useInView({threshold:1,triggerOnce:true});
    const titreCompetence =useRef();
    const titreCompetencePersonnelles = useRef();
    const travailEquipe =useRef();

    useEffect(() => {
      if(visible15){
        titreCompetence.current.classList.add("ApparitionTitreCompentence");

        setTimeout(() => {
            titreCompetencePersonnelles.current.classList.add("ApparitionTitreCompentencePerso");
        }, 700);

        setTimeout(() => {
            travailEquipe.current.classList.add("CompentencePersoVisible");
        }, 700);
       
      }
    }, [visible15])
    

  return (
    <div className='blocCompetence'>
        <h1 ref={titreCompetence} className='titreCompetence invisible'>Mes compétences</h1>
        <div className='competence'>
            <div className='competencePersonnelles'>
                <p ref={titreCompetencePersonnelles} className='titreCompetencePerso invisible'> Compétences personnelles</p>
                <div className='DivCompetence1'>
                    <p className='invisible'>Travail d'équipe</p>
                    <p className='invisible'>Communication</p>
                </div>
                <div className='DivCompetence2'>
                    <p className='invisible'>Créativité</p>
                    <p className='invisible'>Organisation</p>
                </div>
                <div className='DivCompetence3'>
                    <p className='invisible'>Rigueur</p>
                    <p className='invisible'>Fiabilité</p>
                </div>
                <div className='DivCompetence4'>
                    <p className='invisible'>Curiosité</p>
                    <p className='invisible'>Polyvalence</p>
                </div>
            </div>

            <div ref={refDivInvisible15} className='competenceDeveloppement'>
                <span className='petitTitreCompetence'> Compétences en développement</span>
                <div>
                    <p className='paraCss'><span>CSS/SCSS/Bootstrap</span></p>
                </div>
                <div>
                    <p className='paraHtml'><span>html</span></p>
                </div>
                <div>
                    <p className='paraJs'><span>javaScript/React</span></p>
                </div>
                <div>
                     <p className='paraMongo'><span>MongoDB</span></p>
                </div>
                <div>
                <p className='paraJava'><span>java</span></p>
                </div>
            </div>
        </div>
        <div className='divFond'></div>
    </div>
  )
}
