import java from'../../../../image/java2.jpg';


export default function Projet() {
const refImgFlecheGauche = useRef();
const refApparitionBoutton=useRef();

const apparaitionBouttonDisparitionImg =()=>{
    refImgFlecheGauche.current.classList.remove('visibleBoutton');
    refImgFlecheGauche.current.classList.add('invisibleBoutton');
    setTimeout(() => {
        refApparitionBoutton.current.classList.remove('invisibleBoutton');
    }, 600);
}

const disparitionBoutton =()=>{
    refApparitionBoutton.current.classList.add('invisibleBoutton');
    refImgFlecheGauche.current.classList.remove('invisibleBoutton');
    refImgFlecheGauche.current.classList.add('visibleBoutton');
}

  return (
    <Fragment>
        <div id='premierProjet'>
            <p className='titreProjet'> Projet css-javascript</p>
            <div id='containerProjet'>
                <div id='containerTexteProjet'>
                    <p>
                    <img className='visibleBoutton' ref={refImgFlecheGauche} src="https://img.icons8.com/ios-filled/50/000000/circled-right-2.png"/>
                    </p>
                </div>
                <div onMouseLeave={disparitionBoutton} onMouseEnter={apparaitionBouttonDisparitionImg} id='ContainerImageProjet'>
                    <img src={jscss}></img>
                    <button className='invisibleBoutton'  ref={refApparitionBoutton}>DÃ©tails</button>
                </div>
            </div>
        </div>

        <div id='deuxiemeProjet'>
            <p className='titreProjetDeux'> Projet css-javascript</p>
        </div>

      

        
    </Fragment>
  )
}
