import React from 'react'
import './contact.css'
import { useRef } from 'react'
import { useState } from 'react';
import { useEffect } from 'react';
import { useInView } from 'react-intersection-observer';

export default function Contact() {

    const { ref:refDivInvisible,inView:visible5 } = useInView({threshold:0,triggerOnce:true});
    const [booleanTitre, setbooleanTitre] = useState(false);
    const refTitreContact =useRef();
    const refLogoContact =useRef();
    const refContainerContacts = useRef();
    const refAdresseContact = useRef();

    useEffect(() => {
        if(visible5){
           
            refContainerContacts.current.classList.add('apparitionContainerContact');
            setTimeout(() => {
                refAdresseContact.current.classList.add('adresseContactVisible');
                refLogoContact.current.classList.add('LogoContactVisible');
            }, 1000);
            setTimeout(() => {
                refTitreContact.current.classList.add('afficherTitreContact');
            }, 2500);
            setTimeout(() => {
                refTitreContact.current.classList.add('cacherTitreContact');
            }, 4500);
            setTimeout(() => {
                refTitreContact.current.classList.remove('cacherTitreContact');
               setbooleanTitre(true);
            }, 5500);
            setTimeout(() => {
                refTitreContact.current.classList.add('afficherTitreContact2');
            }, 6000);
        }
    }, [visible5])


  return (
    <section>
        <div ref={refContainerContacts}  className='contactBasDePage invisibleContact'>
            <div className='invisibleContact' ref={refTitreContact} id='contactTitre1'> {booleanTitre==false? "Mon profil vous plaît?" : "Contactez-moi"} </div>
            <div className='invisibleContact' ref={refLogoContact} id="logoContact">
                <div>
                    <a><img src="https://img.icons8.com/ios-filled/50/000000/apple-mail.png"/></a>
                </div>
                <div>
                    <img src="https://img.icons8.com/ios-glyphs/30/000000/linkedin-circled--v1.png"/>
                    
                </div>
                <div>
                    <img src="https://img.icons8.com/fluency-systems-filled/48/000000/phone-book.png"/>
                </div>
            </div>
            <div className='invisibleContact' ref={refAdresseContact} id="adresseContact">
                <p>aurelien.peria@gmail.com</p> 
                <p> linkedin</p>
                <p>telephone</p>
            </div>
            <div ref={refDivInvisible} id='divInvisible'>
                
            </div>
        </div>
    </section>
  )
}
