import React from 'react'
import { Fragment } from 'react'
import '../projet/projet.css';
import jscss from'../../../../image/js2.jpg';
import { useRef } from 'react';
import java from'../../../../image/java2.jpg';
import reacts from'../../../../image/react3.avif';
import { useInView } from 'react-intersection-observer';
import { useEffect } from 'react';

export default function Projet() {
const refImgFlecheGauche1 = useRef();
const refImgFlecheGauche2 = useRef();
const refImgFlecheDroite = useRef();

const refApparitionBoutton1=useRef();
const refApparitionBoutton2=useRef();
const refApparitionBoutton3=useRef();

const refContainerTexteProjet1=useRef();
const refContainerTexteProjet2=useRef();

const refContainerImg1=useRef();
const refTitreProjet1 =useRef();

const { ref:refImgContainer1,inView:visible1 } = useInView({threshold:1});
const { ref:refImgContainer2,inView:visible2 } = useInView({threshold:1});
const { ref:refImgContainer3,inView:visible3 } = useInView({threshold:1});


useEffect(() => {
   if(visible1){
    refTitreProjet1.current.classList.add('apparitionTitreScroll');
    refContainerTexteProjet1.current.classList.add('apparitionFlecheScroll');
    refContainerImg1.current.classList.add('apparitionContainerImg');
   }
}, [visible1])

const apparaitionBouttonDisparitionImg1 =()=>{
    refImgFlecheGauche1.current.classList.remove('visibleBoutton');
    refImgFlecheGauche1.current.classList.add('invisibleBoutton');
    refApparitionBoutton1.current.classList.remove('invisibleBoutton');
}

const apparaitionBouttonDisparitionImg2 =()=>{
    refImgFlecheDroite.current.classList.remove('visibleBoutton');
    refImgFlecheDroite.current.classList.add('invisibleBoutton');
    refApparitionBoutton2.current.classList.remove('invisibleBoutton');
}

const apparaitionBouttonDisparitionImg3 =()=>{
    refImgFlecheGauche2.current.classList.remove('visibleBoutton');
    refImgFlecheGauche2.current.classList.add('invisibleBoutton');
    refApparitionBoutton3.current.classList.remove('invisibleBoutton');
}

const disparitionBoutton1 =()=>{
    refApparitionBoutton1.current.classList.remove('visibleBoutton');
    refApparitionBoutton1.current.classList.add('invisibleBoutton');
    refImgFlecheGauche1.current.classList.remove('invisibleBoutton');
    refImgFlecheGauche1.current.classList.add('visibleBoutton');
}

const disparitionBoutton2 =()=>{
    refApparitionBoutton2.current.classList.remove('visibleBoutton');
    refApparitionBoutton2.current.classList.add('invisibleBoutton');
    refImgFlecheDroite.current.classList.remove('invisibleBoutton');
    refImgFlecheDroite.current.classList.add('visibleBoutton');
}

const disparitionBoutton3 =()=>{
    refApparitionBoutton3.current.classList.remove('visibleBoutton');
    refApparitionBoutton3.current.classList.add('invisibleBoutton');
    refImgFlecheGauche2.current.classList.remove('invisibleBoutton');
    refImgFlecheGauche2.current.classList.add('visibleBoutton');
}

  return (
    <Fragment>
        <div id='premierProjet'>
            <p className='containerInvisible' ref={refTitreProjet1} id='titreProjet'> Projet css-javascript</p>
            <div className='containerProjet'>
                <div ref={refContainerTexteProjet1} id="containerTexteProjet1" className='containerTexteProjet containerInvisible'>
                    <p>
                    <img className='visibleBoutton' ref={refImgFlecheGauche1} src="https://img.icons8.com/ios-filled/50/000000/circled-right-2.png"/>
                    </p>
                </div>
                <div  ref={refContainerImg1} className="containerInvisible"  id='ContainerImageProjet' onMouseLeave={disparitionBoutton1} onMouseEnter={apparaitionBouttonDisparitionImg1}>
                    <img   src={jscss}></img>
                    <button className='invisibleBoutton'  ref={refApparitionBoutton1}>Détails</button>
                </div>
            </div>
        </div>

        <div ref={refImgContainer1} className='inferieur1'></div>

        <div id='deuxiemeProjet'>
            <p ref={refContainerTexteProjet2} id='titreProjetDeux'> Projet java</p>
            <div className='containerProjet'>
                <div id='ContainerImageProjetDeux' onMouseEnter={apparaitionBouttonDisparitionImg2} onMouseLeave={disparitionBoutton2}  >
                    <img src={java}></img>
                    <button ref={refApparitionBoutton2} className='invisibleBoutton' >Détails</button>
                </div>
                <div className='containerTexteProjet'>
                    <p>
                    <img className='visibleBoutton' ref={refImgFlecheDroite} src="https://img.icons8.com/ios-filled/50/000000/circled-left-2.png"/>
                    </p>
                </div>
            </div>
        </div>

        <div ref={refImgContainer2} className='inferieur1'></div>

        <div id='troisiemeProjet'>
            <p id='titreProjetTrois'> Projet React</p>
            <div className='containerProjet'>
                <div className='containerTexteProjet'>
                    <p>
                    <img className='visibleBoutton' ref={refImgFlecheGauche2} src="https://img.icons8.com/ios-filled/50/000000/circled-left-2.png"/>
                    </p>
                </div>
                <div  id='ContainerImageProjetTrois'onMouseLeave={disparitionBoutton3} onMouseEnter={apparaitionBouttonDisparitionImg3}>
                    <img src={reacts}></img>
                    <button className='invisibleBoutton'  ref={refApparitionBoutton3}>Détails</button>
                </div>
            </div>
        </div>

        <div ref={refImgContainer3} className='inferieur1'></div>

      

        
    </Fragment>
  )
}
