import React from 'react'
import './contact.css'
import { useRef } from 'react'
import { useState } from 'react';
import { useEffect } from 'react';
import { useInView } from 'react-intersection-observer';

export default function Contact() {

    const { ref:refDivInvisible,inView:visible5 } = useInView({threshold:0,triggerOnce:true});
    const [booleanTitre, setbooleanTitre] = useState(false);
    const refTitreContact =useRef();
    const refLogoContact1 =useRef();
    const refLogoContact2 =useRef();
    const refLogoContact3 =useRef();
    const refContainerContacts = useRef();

    useEffect(() => {
        if(visible5){
           
            refContainerContacts.current.classList.add('apparitionContainerContact');
            setTimeout(() => {
                // refLogoContact.current.classList.add('LogoContactVisible');
            }, 1000);
            setTimeout(() => {
                refTitreContact.current.classList.add('afficherTitreContact');
            }, 2500);
            setTimeout(() => {
                refTitreContact.current.classList.add('cacherTitreContact');
            }, 3750);
            setTimeout(() => {
                refTitreContact.current.classList.remove('cacherTitreContact');
               setbooleanTitre(true);
            }, 4750);
            setTimeout(() => {
                refTitreContact.current.classList.add('afficherTitreContact2');
            }, 5250);
        }
    }, [visible5])


  return (
    <section>
        <div ref={refContainerContacts}  className='contactBasDePage invisibleContact'>
            <div className='invisibleContact' ref={refTitreContact} id='contactTitre1'> {booleanTitre==false? "Mon profil vous plaît ?" : "Contactez-moi"} </div>
            <div  id="logoContact">
                <div>
                    <a target="blank"  href="mailto:aurelien.peria@gmail.com"><img src="https://img.icons8.com/ios-filled/50/000000/apple-mail.png"/></a>
                </div>
                <div>
                    <a target="blank"  href="https://www.linkedin.com/in/aur%C3%A9lien-peria-424224187/"><img src="https://img.icons8.com/ios-glyphs/30/000000/linkedin-circled--v1.png"/></a>        
                </div>
                <div>
                    <a target="blank"  href="https://github.com/bodyraul?tab=repositories"><img src="https://img.icons8.com/ios-filled/50/000000/github.png"/></a>
                </div>
            </div>
            <div ref={refDivInvisible} id='divInvisible'>
                
            </div>
        </div>
    </section>
  )
}
