import React from 'react'
import '../englobphototitres/englobphototitres.css'
import photoprofil from'../../../../../src/image/profil.jpg'
import { useRef } from 'react'
import { useEffect } from 'react'
export default function Englobphototitres() {
  const premierSoulignement =useRef();
  const deuxiemeSoulignement =useRef();
  const titreMetierHover=useRef();
  
  useEffect(() => {
    
     
    setTimeout(() => {
      premierSoulignement.current.classList.remove('avantPremierChargement');
      premierSoulignement.current.classList.add('premierTitreChargement');
    }, 3800);
    setTimeout(() => {
      deuxiemeSoulignement.current.classList.remove('avantPremierChargement');
      deuxiemeSoulignement.current.classList.add('premierTitreChargement');
    }, 4300);
    setTimeout(() => {
      premierSoulignement.current.classList.remove('premierTitreChargement');
    }, 4300);
    setTimeout(() => { 
      deuxiemeSoulignement.current.classList.remove('premierTitreChargement');
    }, 4800);

  }, [])

  return (
    <div className='englobphototitre'>
        <div id='containerAnim'>
          <div id='containerphoto'>
            <img src={photoprofil}>
            </img>
            <p>aurélien péria</p>
            <p>développeur d'applications web</p>
          </div>
        </div>
        <div ref={titreMetierHover}  className='titremetier'>
            <p  id='premiertitre'>
                <span>D</span><span>é</span><span>v</span><span>e</span><span>l</span><span>o</span><span>p</span><span>p</span><span>e</span><span>u</span><span>r</span> <span>c</span><span>r</span><span>é</span><span>a</span><span>t</span><span>i</span><span>f</span>
                <p className='avantPremierChargement' ref={premierSoulignement}></p>
            </p>
            <p  id='deuxiemetitre'>
                <span>P</span><span>a</span><span>s</span><span>s</span><span>i</span><span>o</span><span>n</span><span>n</span><span>é</span> <span>d</span><span>e</span> <span>d</span><span>e</span><span>s</span><span>i</span><span>g</span><span>n</span>
                <p className='avantPremierChargement' ref={deuxiemeSoulignement}></p>
            </p>
        </div>
        <div></div>
      </div>
  )
}
