import React from 'react'
import '../menu/menu.css'
import { useRef } from 'react'

export default function Menu() {
  const animliencontactenter=(e)=>{
    ref.current.classList.add('contactanimenter');
   
  }
  const animliencontactleave=(e)=>{
    ref.current.classList.add('contactanimleave');
    setTimeout(() => {
      ref.current.classList.remove('contactanimleave');
      ref.current.classList.remove('contactanimenter');
    }, 300);
  }

  const refcontact=useRef();
  return (
    <div className='menu'>
          <p>aurelien peria </p>
          <p>Réside à Sannois</p>
          <p>
            <a onMouseLeave={animliencontactleave} onMouseEnter={animliencontactenter} id='liencontact'>Contact<p ref={refcontact}></p></a> 
            
            <a id='lienprojet'>Projet<p></p></a>
          
          </p>
      </div>
  )
}
