import React from 'react'
import '../menu/menu.css'
import { useRef } from 'react'

export default function Menu() {
  const animliencontactenter=(e)=>{
    ref.current.classList.add('contactanimenter');
    console.log(ref);
  }
  const animliencontactleave=(e)=>{
    ref.current.classList.add('contactanimleave');
    console.log(ref);
  }

  const ref=useRef();
  return (
    <div className='menu'>
          <p>aurelien peria </p>
          <p>Réside à Sannois</p>
          <p>
            <a onMouseLeave={animliencontactleave} onMouseEnter={animliencontactenter} id='liencontact'>Contact<p ref={ref}></p></a> 
            
            <a id='lienprojet'>Projet</a>
          
          </p>
      </div>
  )
}
