import React from 'react'
import './contact.css'
import { useRef } from 'react'
import { useState } from 'react';
import { useEffect } from 'react';
import { useInView } from 'react-intersection-observer';

export default function Contact() {

    const { ref:refDivInvisible,inView:visible5 } = useInView({threshold:1});
    const [texteContactTitre, settexteContactTitre] = useState("Mon profil vous plaît?");
    const contactTitre=useRef();
    const refContainerContacts = useRef();


    useEffect(() => {
        if(visible5){
            refContainerContacts.current.classList.add('apparitionContainerContact');
        }
    }, [visible5])

  return (
    <section>
        <div ref={refContainerContacts}  className='contactBasDePage'>
            <div  id='contactTitre1'> Contactez-moi  </div>
            <div id="logoContact">
                <div>
                    <img src="https://img.icons8.com/ios-filled/50/000000/apple-mail.png"/>
                </div>
                <div>
                    <img src="https://img.icons8.com/ios-glyphs/30/000000/linkedin-circled--v1.png"/>
                    
                </div>
                <div>
                    <img src="https://img.icons8.com/fluency-systems-filled/48/000000/phone-book.png"/>
                </div>
            </div>
            <div id="adresseContact">
                <p>aurelien.peria@gmail.com</p>
                <p> linkedin</p>
                <p>telephone</p>
            </div>
            <div ref="refDivInvisible" id='divInvisible'>
                
            </div>
        </div>
    </section>
  )
}
